        private void RequestRate(double seconds, [CallerMemberName]string methodName = null)
        {
            var correction = IsAuthenticated ? 0.3 : 2;
            Limiter.Delay(TimeSpan.FromSeconds(seconds + correction), methodName);
        }

        /// <summary>
