        {
            get { return contentType; }
            set { contentType = value; }
        }

        internal T Deserialize<T>(string content)
        {
            using (var scope = JsConfig.BeginScope())
            {
                scope.AlwaysUseUtc = false;
                scope.AssumeUtc = false;
